rule "parse systemd-journal logs from sidecars messages"

when
    has_field("message") && has_field("SourceModuleName") && 
    to_string($message.SourceModuleName) == "systemd-journal"

then
    //Debug
   // set_field(field: "message_original", value: $message.message);

    let parsed_json_tree = parse_json(value: to_string($message.message));
    let parsed_fields = select_jsonpath(
                            json: parsed_json_tree, 
                            paths:{
                                message: "$.MESSAGE",
                                log_level_num: "$.PRIORITY",
                                facility_num: "$.SYSLOG_FACILITY",
                                application_name: "$.SYSLOG_IDENTIFIER",
                                timestamp_log: "$.__REALTIME_TIMESTAMP",
                                transport: "$._TRANSPORT",
                                cmd_line: "$._CMDLINE",
                                unit_systemd: "$._SYSTEMD_UNIT",
                                unit_user: "$.USER_UNIT",
                                unit: "$.UNIT",
                                user: "$.USER",
                                user_id: "$.USER_ID",
                                kernel_device: "$._KERNEL_DEVICE",
                                kernel_sybsytem: "$._KERNEL_SUBSYSTEM"
                            }
                        ); 
    set_fields(fields: parsed_fields);

    let timestamp_log_milliseconds = to_long($message.timestamp_log) / 1000;
    let parse_timestamp_log = parse_unix_milliseconds(
                                value: timestamp_log_milliseconds, 
                                timezone: to_string("Europe/Moscow")
                               ); 
    let formatted_timestamp_log = format_date(
                                    value: parse_timestamp_log, 
                                    format: "yyyy-MM-dd HH:mm:ss.SSS ZZ", 
                                    timezone: to_string("Europe/Moscow")
                                   );
    set_field(field: "timestamp_log", value: formatted_timestamp_log);
end

rule "add formatted timestamp_log field to ONE messages"

when
    has_field("timestamp_log")

then
    let removed_double_space_timestamp_log = regex_replace(
                                                pattern: "  ", 
                                                value: to_string($message.timestamp_log), 
                                                replacement: " " 
                                             );
    let parsed_timestamp_log = parse_date(
                                value: removed_double_space_timestamp_log, 
                                pattern: "E MMM d HH:mm:ss yyyy", 
                                timezone: to_string("Europe/Moscow")
                              );
    let formatted_timestamp_log = format_date(
                                    value: parsed_timestamp_log, 
                                    format: "yyyy-MM-dd HH:mm:ss.SSS ZZ", 
                                    timezone: to_string("Europe/Moscow")
                                  );
    set_field(field: "timestamp_log", value: formatted_timestamp_log);
end

rule "add formatted log_level field to ONE messages"

when
    has_field("log_level")

then
    let formatted_log_level = lookup_value(
                                lookup_table: "l-t-one-loglevel", 
                                key: to_string($message.log_level)
                              );
    set_field(field: "log_level", value: formatted_log_level);
end

rule "add application_name field (if missed) to systemd-journal logs messages"

when
    has_field("message") && has_field("unit_systemd") && ! has_field("application_name")

then
    let parsed_application_name_field = grok(
                                            pattern: "%{UNITNAME_FROM_SYSTEMD_UNIT}", 
                                            value: to_string($message.unit_systemd), 
                                            only_named_captures: true
                                        );
    set_fields(fields: parsed_application_name_field);
end

rule "add parsed fields to ONE Sunstone/OneFlow messages"

when
    to_string($message.application_name) == "sunstone" ||
    to_string($message.application_name) == "oneflow"

then
    let parsed_fields = grok(
                            pattern: "%{ONE_SS_OF_MSG}", 
                            value: to_string($message.message), 
                            only_named_captures: true
                        );
    set_fields(parsed_fields);
end

rule "add vm_id and vm_name fields to ONE messages"

when
    to_string($message.application_name) == "virtualmachine"

then
    let parsed_vm_id = regex(
                        pattern: "([0-9]+)(?=.log$)", 
                        value: to_string($message.FileName), 
                        group_names: ["id"]
                       );
    set_field(field: "vm_id", value: parsed_vm_id["id"]);

    let vm_name = lookup_value(
                    lookup_table: "l-t-one-vms", 
                    key: to_string($message.vm_id)
                  );
    set_field(field: "virtual_machine_name", value: vm_name);
end

rule "add parsed fields to ONE FireEdge messages"

when
    to_string($message.application_name) == "fireedge"

then
    let parsed_fields = grok(
                            pattern: "%{ONE_SS_CLIENT_TIMESTAMP} %{ONE_FE_MSG:message}", 
                            value: to_string($message.message), 
                            only_named_captures: true
                        );
    set_fields(fields: parsed_fields);
end

rule "add application_name field to NXLog messages from FileName field"

when
    regex(
        pattern: "([0-9]+)(?=.log$)", 
        value: to_string($message.FileName)
    ).matches == false

then
    let message_field = to_string($message.FileName);
    let application_name = grok(
                            pattern: "%{LOGNAME_FROM_PATH}", 
                            value: message_field, 
                            only_named_captures: true
                           );
    set_fields(application_name);
end

rule "add zone_name field to ONE messages"

when
    has_field("zone_id")

then
    let zone_name = lookup_value(
                        lookup_table: "l-t-one-zone", 
                        key: to_string($message.zone_id)
                    );
    set_field(field: "zone_name", value: zone_name);
end

rule "add formatted timestamp_http_reques field to ONE messages"

when
    has_field("timestamp_http_request")

then
    let parsed_timestamp_http_request = parse_date(
                                            value: to_string($message.timestamp_http_request), 
                                            pattern: "d/MMM/yyyy:HH:mm:ss Z", 
                                            timezone: to_string("Europe/Moscow")
                                        );
    let formatted_timestamp_http_request = format_date(
                                            value: parsed_timestamp_http_request, 
                                            format: "yyyy-MM-dd HH:mm:ss.SSS ZZ", 
                                            timezone: to_string("Europe/Moscow")
                                           );
    set_field(field: "timestamp_http_request", value: formatted_timestamp_http_request);
end
